import React, { Component } from 'react'

export default class Try extends Component {
  constructor() {
    super();
    this.state = {
      box: 'Cedcoss',
    }
  }
  handler = event => {
    this.setState({
      box: event.target.value
    });

  }
  render() {
    return (
      <div>
        <input type='text' value={this.state.box} onChange={this.handler} />
        <input type='button' value='click me' />
      </div>
    )
  }
}
