import React, { Component } from 'react'

export default class Counter extends Component {
  constructor() {
    super();
    this.state = {
      count: 0
    };
  }
  decrease = (event) => {
    this.setState({
      count: event.target.value--,
    })
  }

  reset = (event) => {
    this.setState({
      count: event.target.value = 0,
    })
  }

  increase = (event) => {
    this.setState({
      count: event.target.value++,
    })
  }

  render() {
    return (

      <div style={{ backgroundColor: 'white', display: 'flex', flexDirection: 'column', justifyContent: 'center', alignItems: 'center', height: '50vw', border: '1px solid red', margin: '10px 180px 0px 180px', padding: '0' }}>

        <h1 style={{ color: 'rgb(18, 18, 93)' }}>Counter</h1>

        <div>
          <h1 style={{ fontSize: '8vw' }}>{this.state.count}</h1>
          <button onClick={this.decrease}>DECREASE</button>
          <button onClick={this.reset}>RESET</button>
          <button onClick={this.increase}>INCREASE</button>
        </div>

      </div>
    )
  }
}
