import React, { Component } from 'react'

export default class Countdown extends Component {
  constructor() {
    super();
    this.state = {
     countdown:new Date()
    }
  }
  CountTime() {
    thidstimerID = setInterval( () => this.tick(), 1000 );
  }
  tick () {
    this.setState({
      date:new Date()
    });
  }

  render() {
    return (
      <div style={{ backgroundColor: 'white', margin: '10px', padding: '20px' }}>
        <h1>{ this.state.countdown.toLocaleTimeString() }</h1>
        <button onClick={ this.CountTime }>start countdown</button>
      </div>
    )
  }
}
