import React, { Component } from 'react'

export default class Countdown extends Component {
  constructor() {
    super();
    this.state = {
     countdown:new Date(),
     temp:new Date(),
     temp1:0,
     temp2:0,
     temp3:0
    };
  }
  CountTime = () => {
    this.timerID = setInterval(
       () => this.tick(),
        1000 
        );
  }
  tick = () => {
    this.setState({
     temp1:this.state.countdown.getHours(),
     temp2:this.state.countdown.getMinutes(),
     temp3:this.state.countdown.getSeconds(),
    });

    if(this.state.temp3 <= 0){
       this.setState({
         temp2:this.state.temp2-1,
         temp3:59
       })
    }
  }

  render() {
    return (
      <div style={{ backgroundColor: 'white', margin: '10px', padding: '20px' }}>
        <h1>{ this.state.countdown.toLocaleTimeString() }</h1>
        <button onClick={ this.CountTime }>start countdown</button>
        <p>{this.state.temp1}</p>
      </div>
    )
  }
}
