    minutes:countdown.getMinutes(),
    seconds:countdown.getSeconds()
    };
  }

  CountTime = () => {
    this.timer = setInterval(
      () => this.task(),
      1000
    );
  }

  task = () => {
    if(this.state.seconds > 0) {
      this.setState({
