import React, { Component } from 'react'

export default class Stopwatch extends Component {
    constructor() {
        super();
        this.state = {
          count:0
        };
      }
  render() {
    return (
      <div style={{backgroundColor:'white', padding:'8vw'}}>
         <div><h1>React Stopwatch</h1></div>
         <h1>{ this.state.count }</h1>
         <div>
             <button >Start</button>
             <button>Pause</button>
             <button>Resume</button>
             <button>Reset</button>
         </div>
      </div>
    )
  }
}
